E\n					wp_blc_synch AS synch\n					JOIN wp_posts AS posts ON (synch.container_id = posts.ID and synch.container_type=posts.post_type)\n				  SET\n					synched = 0\n				  WHERE\n					synch.last_synch < posts.post_modified\";i:2;N;}i:43;a:3:{i:0;i:0;i:1;s:38:\"...... 0 rows updated in 0.001 seconds\";i:2;N;}i:44;a:3:{i:0;i:0;i:1;s:43:\"...... Creating synch records for new posts\";i:2;N;}i:45;a:3:{i:0;i:0;i:1;s:389:\"INSERT INTO wp_blc_synch(container_id, container_type, synched)\n				  SELECT posts.id, posts.post_type, 0\n				  FROM\n				    wp_posts AS posts LEFT JOIN wp_blc_synch AS synch\n					ON (synch.container_id = posts.ID and synch.container_type=posts.post_type)\n				  WHERE\n				  	posts.post_status IN (\'publish\')\n	 				AND posts.post_type IN (\'post\', \'page\')\n					AND synch.container_id IS NULL\";i:2;N;}i:46;a:3:{i:0;i:0;i:1;s:39:\"...... 0 rows inserted in 0.000 seconds\";i:2;N;}i:47;a:3:{i:0;i:0;i:1;s:27:\"... Notifying module \"page\"\";i:2;N;}i:48;a:3:{i:0;i:0;i:1;s:74:\"...... Skipping \"page\" resyncyh since all post types were already synched.\";i:2;N;}i:49;a:3:{i:0;i:0;i:1;s:38:\"... Notifying module \"youtube-checker\"\";i:2;N;}i:50;a:3:{i:0;i:0;i:1;s:37:\"... Notifying module \"youtube-iframe\"\";i:2;N;}i:51;a:3:{i:0;i:0;i:1;s:28:\"... Notifying module \"dummy\"\";i:2;N;}i:52;a:3:{i:0;i:1;i:1;s:24:\"--- Total: 0.009 seconds\";i:2;N;}i:53;a:3:{i:0;i:1;i:1;s:38:\"Updating server load limit settings...\";i:2;N;}i:54;a:3:{i:0;i:1;i:1;s:57:\"Disable load limit. Cannot retrieve current load average.\";i:2;N;}i:55;a:3:{i:0;i:1;i:1;s:26:\"Optimizing the database...\";i:2;N;}i:56;a:3:{i:0;i:1;i:1;s:24:\"--- Total: 0.130 seconds\";i:2;N;}i:57;a:3:{i:0;i:1;i:1;s:26:\"Completing installation...\";i:2;N;}i:58;a:3:{i:0;i:1;i:1;s:20:\"Configuration saved.\";i:2;N;}i:59;a:3:{i:0;i:1;i:1;s:78:\"Installation/update completed at 2022-08-23 08:45:14 with 25 queries executed.\";i:2;N;}i:60;a:3:{i:0;i:1;i:1;s:25:\"Total time: 0.274 seconds\";i:2;N;}}', 'yes'),
